var punctuationPOS = Object.create( null );
punctuationPOS[ '.' ] = '.';
punctuationPOS[ '!' ] = '.';
punctuationPOS[ '?' ] = '.';
punctuationPOS[ '[' ] = '-LRB-';
punctuationPOS[ '{' ] = '-LRB-';
punctuationPOS[ '(' ] = '-LRB-';
punctuationPOS[ ']' ] = '-RRB-';
punctuationPOS[ '}' ] = '-RRB-';
punctuationPOS[ ')' ] = '-RRB-';
punctuationPOS[ ',' ] = ',';
punctuationPOS[ ':' ] = ':';
punctuationPOS[ ';' ] = ':';
punctuationPOS[ '--' ] = ':';
punctuationPOS[ '-' ] = 'HYPH';
punctuationPOS[ '…' ] = ':';
punctuationPOS[ '...' ] = ':';
punctuationPOS[ '"' ] = '"';
punctuationPOS[ '\'' ] = '"';

module.exports = punctuationPOS;
